-- Create "judges" table
CREATE TABLE "public"."judges" ("id" character varying(64) NOT NULL, "problem" character varying(64) NOT NULL, "algorithm" text NOT NULL, "success" boolean NOT NULL, "user" character varying(64) NOT NULL, "failed_reason" text NULL, "created_at" integer NOT NULL DEFAULT public.get_timestamp(), "updated_at" integer NOT NULL DEFAULT public.get_timestamp(), PRIMARY KEY ("id"), CONSTRAINT "problem" FOREIGN KEY ("problem") REFERENCES "public"."problems" ("id") ON UPDATE NO ACTION ON DELETE CASCADE, CONSTRAINT "user" FOREIGN KEY ("user") REFERENCES "public"."users" ("id") ON UPDATE NO ACTION ON DELETE CASCADE);
