-- Create "comments" table
CREATE TABLE "public"."comments" ("id" character varying(64) NOT NULL, "problem" character varying(64) NOT NULL, "text" text NOT NULL, "created_by" character varying(64) NOT NULL, "created_at" integer NOT NULL DEFAULT public.get_timestamp(), "updated_at" integer NOT NULL DEFAULT public.get_timestamp(), PRIMARY KEY ("id"), CONSTRAINT "created_by" FOREIGN KEY ("created_by") REFERENCES "public"."users" ("id") ON UPDATE NO ACTION ON DELETE CASCADE, CONSTRAINT "problem" FOREIGN KEY ("problem") REFERENCES "public"."problems" ("id") ON UPDATE NO ACTION ON DELETE CASCADE);
